const data = msg.payload;

msg.payload = {
    bucket: 'DB',
    precision: 'ms',
    token: '123456789',
    method: 'POST',
    data: []
};

// -----------------------------------------
// Einstellbare Parameter
const peakStrengthFactor = 1.5; // z.B. 1.0 = normal, 2.0 = doppelt so stark wie der Maximalwert
const idwFlatteningPower = 2.0; // z.B. 2.0 = Standard, 1.2 = abgeflacht, 4.0 = steiler
const gridSize = 10;
// -----------------------------------------

const baseTimestamp = Number(data.timestamp) * 1000; // convert to ms
const baseSequence = data.sequence;
const values = data.value;
let heatmapArr = new Uint8Array(100);
const sensorValues = {};

// No loop for better readability
sensorValues[0] = Number(values[0]);
sensorValues[1] = Number(values[10]);
sensorValues[2] = Number(values[20]);
sensorValues[3] = Number(values[30]);

const sensorPositions = {
    0: { x: 0, y: 0 },
    1: { x: 1, y: 0 },
    2: { x: 0, y: 1 },
    3: { x: 1, y: 1 },
};

// WCL Peak-Bestimmung
const epsilon = 0.0001;
let sumWeightedX = 0;
let sumWeightedY = 0;
let sumWeights = 0;

for (const [key, value] of Object.entries(sensorValues)) {
    const pos = sensorPositions[key];
    const weight = value;
    sumWeightedX += weight * pos.x;
    sumWeightedY += weight * pos.y;
    sumWeights += weight;
}

const peakX = sumWeightedX / (sumWeights || epsilon);
const peakY = sumWeightedY / (sumWeights || epsilon);
const peakValue = Math.max(...Object.values(sensorValues)) * peakStrengthFactor;

// Interpolationspunkte inkl. virtuellem Peak
const interpolationPoints = [
    ...Object.entries(sensorPositions).map(([key, pos]) => ({
        x: pos.x,
        y: pos.y,
        value: sensorValues[key],
    })),
    { x: peakX, y: peakY, value: peakValue },
];

// IDW Interpolation
let arrIndex = 0;
for (let row = 0; row < gridSize; row++) {
    const y = row / (gridSize - 1);

    for (let col = 0; col < gridSize; col++) {
        const x = col / (gridSize - 1);
        let numerator = 0;
        let denominator = 0;

        for (const point of interpolationPoints) {
            const dx = x - point.x;
            const dy = y - point.y;
            const distance = Math.sqrt(dx * dx + dy * dy) || epsilon;
            const weight = 1 / Math.pow(distance, idwFlatteningPower);
            numerator += point.value * weight;
            denominator += weight;
        }

        const interpolatedValue = numerator / denominator;
        const downscaledValue = Math.floor(interpolatedValue/32);
        heatmapArr[arrIndex] = downscaledValue;
        arrIndex++;
    }
}

const base64Data = Buffer.from(heatmapArr).toString('base64');

msg.payload.data.push({
    measurement: 'heatmap_arr',
    fields: {
        base64: base64Data,
        peakX: peakX,
        peakY: peakY,
        peakValue: peakValue
    },
    timestamp: baseTimestamp,
});

return msg;

